return {
  json: {
    symbol: "SOLUSDT",
    price: $node["Fetch_1m"].json.Data.Data.slice(-1)[0].close,
    data_1m: $node["Fetch_1m"].json.Data.Data,
    data_5m: $node["Fetch_5m"].json.Data.Data,
    data_1h: $node["Fetch_1h"].json.Data.Data
  }
};

// 1Ô∏è‚É£ Get raw text from AI Agent output
const rawOutput = $node["AI Agent"].json.output;

// 2Ô∏è‚É£ Validate that something exists
if (!rawOutput) {
  throw new Error("AI Agent output is empty or undefined.");
}

// 3Ô∏è‚É£ Extract the JSON portion between ```json ... ```
const jsonMatch = rawOutput.match(/```json([\s\S]*?)```/);

if (!jsonMatch) {
  throw new Error("No valid JSON found in AI Agent output. Make sure the AI returns data inside ```json ... ```");
}

// 4Ô∏è‚É£ Parse the extracted JSON content
let parsedData;
try {
  parsedData = JSON.parse(jsonMatch[1].trim());
} catch (err) {
  throw new Error("Failed to parse JSON from AI Agent output: " + err.message);
}

// 5Ô∏è‚É£ Return as parsed object for downstream nodes (e.g., Telegram)
return [parsedData];

